var orderId = pm.variables.get("OrderId");
var body = pm.response.json();

// Status code kontrolü
pm.test(`✅ Negative test for invalid orderId (${orderId}) - Status code`, function () {
    pm.expect(pm.response.code).to.be.oneOf([400, 404, 422]);
});

// Hata mesajı kontrolü
pm.test(`✅ Error message for invalid orderId (${orderId})`, function () {
    pm.expect(body).to.have.property("message");
    pm.expect(body.message).to.eql("Order not found");
});

// Console log
console.log(`Order ID ${orderId} test result:`);
console.log("Status code:", pm.response.code);
console.log("Response body:", pm.response.text());
